mapper.sql:
1=1

and mvn_fclty_nm like '%'||#{param.mvnFcNm}||'%'


and mvn_fclty_ty_code = #{param.mvnFcType}


and mvn_fclty_dlyp_code = #{param.mvnFcDtype}


and use_at = #{param.enabled}

mapper.select:
/** UsptMvnFcltyInfoDao.selectList_count */ select
, #{totalItems} + 1 - (row_number() over(order by creat_dt desc)) as rn from mvn_api.uspt_mvn_fclty_info where
offset #{beginRowNum} - 1 limit #{itemsPerPage}

mapper.sql:
a.mvn_fclty_ty_code = 'OFFICE'

and a.mvn_sttus_code = #{param.mvnSt}


and a.bulddong_code = #{param.bnoCd}


and a.mvn_fclty_aceptnc_nmpr = #{param.mvnFcCapacity}


and a.mvn_fclty_ar = #{param.mvnFcar}

mapper.select:
/** UsptMvnFcltyInfoDao.selectEnable */
select

from mvn_api.uspt_mvn_fclty_info
where
	mvn_fclty_ty_code = #{mvnFcType}
	and use_at = true
	and mvn_fclty_id = #{mvnFcId}

mapper.sql:
mvn_fclty_ty_code = 'SHARE'
and use_at = true

and mvn_fclty_dlyp_code = #{mvnFcDtype}

mapper.select:
/** UsptMvnFcltyInfoDao.selectEnableShareList */ select
, #{totalItems} + 1 - (row_number() over(order by mvn_fclty_dlyp_code)) as rn from mvn_api.uspt_mvn_fclty_info where
order by mvn_fclty_dlyp_code offset #{beginRowNum} - 1 limit #{itemsPerPage}

mapper.sql:
mvn_fclty_ty_code = 'OFFICE' and mvn_sttus_code = 'EMPTY' and use_at = true
and bulddong_code = split_part(#{bnoRoomNo}, '|-|', 1) and room_no like split_part(#{bnoRoomNo}, '|-|', 2)
and mvn_fclty_aceptnc_nmpr = #{mvnFcCapacity}
and mvn_fclty_ar = #{mvnFcar}

mapper.select:
/** UsptMvnFcltyInfoDao.selectEnableOfficeList */ select
, #{totalItems} + 1 - (row_number() over(order by bulddong_code, room_no)) as rn from mvn_api.uspt_mvn_fclty_info umf where
order by bulddong_code, room_no offset #{beginRowNum} - 1 limit #{itemsPerPage}
