export const Pagination: React.FC<{
  curPage: number;
  totalPage: number;
  rowsPerPage: number;
  handleChangeRowsPerPage: (event: any) => void;
  handleChangePage: (event: unknown, newPage: number) => void;
  hideRowPerPage?: boolean;
  style?: React.CSSProperties;
  setCurPage?: Dispatch<SetStateAction<number>>
}> = (props) => {

  useEffect(() => {
    if (!!props.rowsPerPage)
      if (!!props.setCurPage)
        props.setCurPage(0)
  },[props.rowsPerPage])

  return <Box style={{paddingTop: "15px", ...props.style}}>
    {
      !props.hideRowPerPage && <Box
            sx={{display: 'flex', color: '#666'}}
            justifyContent={'right'}
            alignItems={'center'}
        >
            <InputLabel
                variant="standard"
                htmlFor="uncontrolled-native"
                sx={{pr: 2}}
            >
                Page {props.curPage + 1} / {props.totalPage}
            </InputLabel>
            <FormControl variant="standard">
                <SelectStyle
                    className="select"
                    value={props.rowsPerPage}
                    onChange={props.handleChangeRowsPerPage}
                >
                  {[5, 10, 15].map((v: any) => (
                    <MenuItem key={v} value={v}>
                      {v}개씩
                    </MenuItem>
                  ))}
                </SelectStyle>
            </FormControl>
        </Box>
    }
    <MUIPagination
      shape="rounded"
      // color="black"
      count={props.totalPage}
      showFirstButton
      showLastButton
      page={props.curPage + 1}
      onChange={props.handleChangePage}
      style={{display: 'flex', justifyContent: 'center'}}
    />
  </Box>
}

